import React from 'react'
import { Link } from "react-router-dom";
import { getSiteImages } from '../../../helpers/api';
export default function Pagination(postsPerPage, totalPosts) {
    console.log(postsPerPage);
    console.log(totalPosts);
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumbers.push(i);
    }
    return (
        <div className="pagination_flex">
            <ul className="pagination">
                {
                    pageNumbers.map(number => (
                        <li key={number}>
                            <a href="#!">{number}</a>
                        </li>
                    ))
                }


                <li>
                    <div class="btn_blk blog_story_btn">
                        <Link to="" className="site_btn border blank">
                            <span>Next </span>
                            <img src={getSiteImages("/images/icon-arrow-right.svg")} alt="Next" />
                        </Link>
                    </div>
                </li>
            </ul>
        </div>
    )
}
