import React, { useState } from "react";

function PostComment() {
    const [formReport, setFormReport] = useState({
        name: "",
        email: "",
        comment: "",
    });
    const handleInput = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setFormReport({ ...formReport, [name]: value });
    };
    return (
        <div className="post_cmnt_form">
            <h2 className="heading">Post a reply</h2>
            <form method="post" id="frmReport" onSubmit={handleInput}>
                <div className="form_row row">
                    <div className="col-xs-6">
                        <div className="form_blk">
                            <p>Your name</p>
                            <input type="text" name="name" id="name" value={formReport.name} onChange={handleInput} className="input" />
                        </div>
                    </div>
                    <div className="col-xs-6">
                        <div className="form_blk">
                            <p>Email Address</p>
                            <input type="text" name="email" id="email" value={formReport.name} onChange={handleInput} className="input" />
                        </div>
                    </div>
                    <div className="col-xs-12">
                        <div className="form_blk">
                            <p>Leave a comment here</p>
                            <textarea name="comments" id="comment" className="input" onChange={handleInput}></textarea>
                        </div>
                    </div>
                </div>
                <div class="btn_blk blog_story_btn text-center">
                    <button class="site_btn border blank" >
                        Sunmit Comment
                    </button>
                </div>
            </form>
        </div>
    )
}
export default PostComment;
